
switch (process.env.NODE_ENV) {
  case 'dev':
    module.exports = require('./development');
    break;
  case 'stage':
    module.exports = require('./staging');
    break;
  case 'prod':
    module.exports = require('./production');
    break;
  default:
    console.error("Unrecognized NODE_ENV: " + process.env.NODE_ENV);
    process.exit(1);
}
