	//run
	//$ DEBUG=i_log:* node ... to see the all messages related the module
	//$ DEBUG=i_log:info node ... to see the info messages related the module 

module.exports = function (logkey){
	var module={};
	module.logKey= logkey || "i-log";
	module.log =	function(msg){ console.log(msg);};
	module.debug = function(msg){ require('debug')(this.logKey+ ':debug')(new Date() + "%j", msg);};
	module.info = function(msg){ require('debug')(this.logKey+ ':info')(new Date()  + "%j", msg); };
	module.error = function(msg){ require('debug')(this.logKey+ ':error')(new Date()  + "%j", msg); };
	return module;
};